import React, { useState } from "react";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/actions";
import { Link } from "react-router-dom";
import { HashLink } from "react-router-hash-link";
import clsx from "clsx";
import "./style.css";
import Avatar from "@material-ui/core/Avatar";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import ListItem from "@material-ui/core/ListItem";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChatIcon from "@material-ui/icons/QuestionAnswerOutlined";
import CursosIcon from "@material-ui/icons/ViewWeek";
import QuestionIcon from "@material-ui/icons/ContactSupport";
import MenuIcon from "@material-ui/icons/Menu";
import LoginIcon from "@material-ui/icons/PermIdentity";
import RegisterICon from "@material-ui/icons/AssignmentTurnedIn";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    color: "black",
    // border: 'none',
    // boxShadow: 'none',
    backgroundColor: "white",
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },

  title: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: "auto",
  },

  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 1,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: "0 8px",
    ...theme.mixins.toolbar,
    justifyContent: "flex-start",
    backgroundColor: "white",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginRight: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginRight: 0,
  },
  avatar: {
    margin: 7,
  },
}));

function MenuBar(props) {
  const { user, isAuthenticated } = props.auth;
  // console.log(isAuthenticated, user);
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false);

  function handleDrawerOpen() {
    setOpen(true);
  }

  function handleDrawerClose() {
    setOpen(false);
  }
  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position="static"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <nav className="navbar navbar-dark bg-dark navi-color text-center">
          *Síguenos en Fb como @Starkclass para más novedades{" "}
        </nav>
        <Toolbar>
          <div className={classes.title}>
            <Link onClick={handleDrawerClose} to="/">
              {" "}
              <img
                src="/assets/img/pene1.svg"
                alt="Logo"
                height="70"
                width="230"
                className="imglogo"
              />
            </Link>
          </div>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="end"
            onClick={handleDrawerOpen}
            className={clsx(open && classes.hide)}
          >
            <MenuIcon fontSize="large" />
          </IconButton>
        </Toolbar>
