import React from "react";
import Countdown from "react-countdown";
export default function TimeCounter({ hr }) {
  console.log(Date.now());
  const Completionist = () => <span>BOo0o0o0M!!!!!</span>;

  const renderer = ({ hours, minutes, seconds, completed }) => {
    if (completed) {
      // Render a complete state
      return <Completionist />;
    } else {
      // Render a countdown
      return (
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-evenly",
          }}
        >
          <span style={{ marginTop: "30px" }}>
            <span
              style={{
                color: "black",
                padding: "5px",
                borderColor: "#FF4900",
                borderStyle: "solid",
                borderRadius: ".5rem",
              }}
            >
              {hours}
            </span>
            <span style={{ color: "#FF4900", fontSize: "3rem" }}> Hr </span>
          </span>
          <span style={{ marginTop: "30px" }}>
            {" "}
            <span
              style={{
                color: "black",
                padding: "5px",
                borderColor: "#FF4900",
                borderStyle: "solid",
                borderRadius: ".5rem",
              }}
            >
              {minutes}
            </span>
            <span style={{ color: "#FF4900", fontSize: "3rem" }}> min </span>
          </span>
          <span style={{ marginTop: "30px" }}>
            {" "}
            <span
              style={{
                color: "black",
                padding: "5px",

                borderColor: "#FF4900",
                borderStyle: "solid",
                borderRadius: ".5rem",
              }}
            >
              {seconds}
            </span>
            <span style={{ color: "#FF4900", fontSize: "3rem" }}> seg </span>
          </span>
        </div>
      );
    }
  };
  return <Countdown date={Date.now() + hr * 3600000} renderer={renderer} />;
}
