import React, { Component } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import { CulqiProvider, Culqi } from "react-culqi";

export default class modal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      cupon: "",
      checked: false,
      amount: 0,
      appliqued: false
    };
  }

  componentWillMount() {
    const { amount } = this.props;
    this.setState({ amount });
  }
  cerrar() {
    this.props.modalCerrar("");
  }

  render() {
    if (this.props.tipo === "yape") {
      return (
        <div className="fondoGris">
          <div
            className="row"
            style={{
              justifyContent: "center",
              alignContent: "center",
              height: "100%"
            }}
          >
            <div className="caja">
              <div
                className="cabeceracaja"
                style={{ justifyContent: "center", alignContent: "center" }}
              >
                <h1>¡Gracias por confiar en nosotros!</h1>
                <div className="cerrar" onClick={this.cerrar.bind(this)}>
                  <img
                    src="./assets/icons/cerrar.png"
                    width="20"
                    alt="cerrar icon"
                  />
                </div>
              </div>
              <div className="bodycaja" align="center">
                <br />
                <p>
                  “Yapea” al número <strong>997 914 757</strong> o escanea el
                  código QR con Yape.
                </p>
                <div className="row" style={{ justifyContent: "center" }}>
                  <div className="col-4" align="center">
                    <img
                      src="./assets/icons/yape.png"
                      className="yape"
                      alt="yape"
                    />
                  </div>
                  <div className="col-4" align="center">
                    <img
                      src="./assets/icons/codigoqr.png"
                      className="yape"
                      alt="yape"
                    />
                  </div>
                </div>
                <p>
                  Luego ENVÍA un mensaje al{" "}
                  <strong>WhatsApp (997 914 757)</strong> o al correo{" "}
                  <strong>(informes@starkclass.com)</strong> con la captura o
                  fotografía del comprobante, además de brindarnos tus nombres
                  completos y correo electrónico del cual te suscribiste.
                </p>
                <br />
                <h4>En menos de una hora, tendrás acceso.</h4>
                <h4>RECUERDA, DIGITAR EL MONTO DE S/ 399.99</h4>
                <h5>
                  Aplican la <Link to="/politica">Política</Link> y los{" "}
                  <Link to="/terminos">Términos de Servicio</Link>
                </h5>
              </div>
            </div>
          </div>
        </div>
      );
    } else if (this.props.tipo === "bcp") {
      return (
        <div className="fondoGris">
          <div
            className="row"
            style={{
              justifyContent: "center",
              alignContent: "center",
              height: "100%"
            }}
          >
            <div className="caja">
              <div
                className="cabeceracaja"
                style={{ justifyContent: "center", alignContent: "center" }}
              >
                <h1>¡Gracias por confiar en nosotros!</h1>
                <div className="cerrar" onClick={this.cerrar.bind(this)}>
                  <img src="./assets/icons/cerrar.png" width="20" alt="" />
                </div>
              </div>
              <div className="bodycaja" align="center">
                <img src="./assets/icons/bcp.png" alt="bcp logo" />
                <br />
                <br />
                <h3>Deposita al número de cuenta:</h3>
                <h3>193-2625326-0-43 (MYCODE S.A.C)</h3>
                <br />
                <p>
                  <strong>
                    Nota: Para depósitos, el monto a desembolsar es de S/ 399.90
                  </strong>
                </p>
                <p>
                  Luego ENVÍA un mensaje al{" "}
                  <strong>WhatsApp (997 914 757)</strong> o al correo{" "}
                </p>
                <p>
                  <strong>(informes@starkclass.com)</strong> con la captura o
                  fotografía del comprobante, además de brindarnos tus nombres
                  completos y correo electrónico del cual te suscribiste.
                </p>
                <p>En menos de una hora, tendrás acceso.</p>

                <h5>
                  Aplican la <Link to="/politica">Política</Link> y los{" "}
                  <Link to="/terminos">Términos de Servicio</Link>
                </h5>
              </div>
            </div>
          </div>
        </div>
      );
    } else if (this.props.tipo === "bcp") {
      return (
        <div className="fondoGris">
          <div
            className="row"
            style={{
              justifyContent: "center",
              alignContent: "center",
              height: "100%"
            }}
          >
            <div className="caja">
              <div
                className="cabeceracaja"
                style={{ justifyContent: "center", alignContent: "center" }}
              >
                <h1>¡Gracias por confiar en nosotros!</h1>
                <div className="cerrar" onClick={this.cerrar.bind(this)}>
                  <img alt="img" src="./assets/icons/cerrar.png" width="20" />
                </div>
              </div>
              <div className="bodycaja" align="center">
                <img alt="img" src="./assets/icons/bcp.png" />
                <br />
                <br />
                <h3>Deposita al número de cuenta:</h3>
                <h3>193-2625326-0-43 (MYCODE S.A.C)</h3>
                <br />
                <p>
                  <strong>
                    Nota: Para depósitos, el monto a desembolsar es de S/ 399.90
                  </strong>
                </p>
                <p>
                  Luego ENVÍA un mensaje al{" "}
                  <strong>WhatsApp (997 914 757)</strong> o al correo{" "}
                </p>
                <p>
                  <strong>(informes@starkclass.com)</strong> con la captura o
                  fotografía del comprobante, además de brindarnos tus nombres
                  completos y correo electrónico del cual te suscribiste.
                </p>
                <p>En menos de una hora, tendrás acceso.</p>

                <h5>
                  Aplican la <Link to="/politica">Política</Link> y los{" "}
                  <Link to="/terminos">Términos de Servicio</Link>
                </h5>
              </div>
            </div>
          </div>
        </div>
      );
    } else if (this.props.tipo === "culqi") {
      return (
        <div className="fondoGris">
          <div
            className="row"
            style={{
              justifyContent: "center",
              alignContent: "center",
              height: "100%"
            }}
          >
            <div
              className="caja"
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                backgroundColor: "#E4E4E4",
                padding: 16,
                width: 500
              }}
            >
              <div
                className="cerrar"
                onClick={this.cerrar.bind(this)}
                style={{ alignSelf: "flex-end" }}
              >
                <img alt="img" src="./assets/icons/cerrar.png" width="20" />
              </div>
              <CulqiProvider
                publicKey="pk_live_QZSp8E5iSDnIZjNp"
                amount={this.state.amount}
                title="Pago de curso"
                description="Compra este curso"
                onToken={token => {
                  console.log("token received", token);
                }}
                onError={error => {
                  console.error("something bad happened", error);
                }}
                // Uncomment `options` to see customizations take place
                options={{
                  style: {
                    maincolor: "#5581E9",
                    buttontext: "white",
                    maintext: "darkorange",
                    desctext: "pink"
                  }
                }}
              >
                <div>
                  <Culqi>
                    {({ openCulqi, setAmount, amount }) => {
                      return (
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "column",
                            justifyContent: "center",
                            alignItems: "center"
                          }}
                        >
                          <h3
                            style={{
                              fontSize: 16,
                              color: "#515151",
                              marginTop: 16,
                              marginBottom: 16
                            }}
                          >
                            ¿Tienes cupón de descuento?
                          </h3>
                          <input
                            style={{
                              width: 280,
                              marginBottom: 16,
                              padding: 6,
                              textAlign: "center"
                            }}
                            className="cupon"
                            disabled={this.state.appliqued}
                            value={this.state.cupon}
                            onChange={event =>
                              this.setState({ cupon: event.target.value })
                            }
                            placeholder="Inserte código Promocional"
                          />
                          {String(this.state.cuponMessage).length > 0 && (
                            <label>{this.state.cuponMessage}</label>
                          )}
                          <button
                            onClick={() => {
                              if (this.state.cupon === "TADE001") {
                                let res = Number(
                                  Number(this.state.amount * 0.7).toFixed()
                                );
                                this.setState({
                                  appliqued: true,
                                  cuponMessage: "Cupón aplicado"
                                });
                                setAmount(res);
                              } else if (this.state.cupon === "TADE002") {
                                let res = this.state.amount - 8000;
                                this.setState({
                                  appliqued: true,
                                  cuponMessage: "Cupón aplicado"
                                });
                                setAmount(res);
                              } else {
                                this.setState({
                                  cuponMessage:
                                    "El cupón que haz ingresado es inválido",
                                  appliqued: false
                                });
                              }
                            }}
                            style={{
                              backgroundColor: "#0aa8ab",
                              color: "white",
                              borderWidth: 0,
                              padding: 6,
                              width: 100,
                              marginBottom: 12,
                              boxShadow: "0px 5px 5px #26323888"
                            }}
                            disabled={
                              this.state.cupon.length > 4 &&
                              !this.state.appliqued
                                ? false
                                : true
                            }
                            className="paymentBtn"
                          >
                            Aplicar
                          </button>
                          <label
                            style={{
                              margin: "10px 0 20px 0"
                            }}
                          >
                            <input
                              type="checkbox"
                              value={this.state.checked}
                              onChange={async e => {
                                await this.setState({
                                  checked: e.target.checked
                                });
                                console.log(this.state.checked);
                              }}
                            />{" "}
                            Acepto la{" "}
                            <font style={{ color: "#0aa8ab" }}>política</font> y
                            los{" "}
                            <font style={{ color: "#0aa8ab" }}>
                              Términos de Servicio
                            </font>
                          </label>
                          <div
                            style={{
                              display: "flex",
                              justifyContent: "space-around",
                              width: 280
                            }}
                          >
                            <button
                              onClick={openCulqi}
                              style={{
                                backgroundColor: "#0aa8ab",
                                color: "white",
                                borderWidth: 0,
                                padding: 6,
                                width: 150,
                                boxShadow: "0px 5px 5px #26323888"
                              }}
                              disabled={!this.state.checked}
                              className="paymentBtn"
                            >
                              Pagar
                            </button>
                          </div>
                        </div>
                      );
                    }}
                  </Culqi>
                </div>
              </CulqiProvider>
            </div>
          </div>
        </div>
      );
    } else {
      return null;
    }
  }
}
