import React, { Component } from "react";
import Footer from "../Footer/footer";
import Modal from "../Modal/modal";

export default class pay extends Component {
  amount = 39999;
  constructor() {
    super();
    this.state = {
      value: ""
    };
  }

  componentDidMount() {
    window.scrollTo(0, 0);
    this.amount = this.props;
  }

  async set(event) {
    if (this.state.value === event.target.value) {
      this.setState({ value: "" });
    } else {
      this.setState({ value: event.target.value });
    }
    console.log(this.state.value);
    console.log(event.target.value);
  }

  render() {
    return (
      <div style={{ backgroundColor: "white" }}>
        <Modal
          tipo={this.state.value}
          modalCerrar={valor => {
            this.setState({ value: valor });
          }}
          amount={this.amount}
        />

        <div className="fondoazul">
          <div className="container">
            <h1>¡Tenemos varios métodos de pago, No te preocupes!</h1>
          </div>
        </div>

        <div
          className="container"
          onChange={this.set.bind(this)}
          style={{ marginTop: 50, marginBottom: 30 }}
        >
          <div className="row" style={{ justifyContent: "center" }}>
            <div
              style={{
                width: 270,
                justifyContent: "center",
                alignContent: "center",
                alignSelf: "center",
                alignItems: "center"
              }}
            >
              <div className="radio">
                <label>
                  <input
                    type="radio"
                    value="yape"
                    name="pagos"
                    checked={this.state.value === "option1"}
                    style={{ marginRight: 20 }}
                  />
                  Yape
                </label>
              </div>
              <div className="radio">
                <label>
                  <input
                    type="radio"
                    value="bcp"
                    name="pagos"
                    checked={this.state.value === "option2"}
                    style={{ marginRight: 20 }}
                  />
                  Depósito bancario
                </label>
              </div>
              <div className="radio">
                <label>
                  <input
                    type="radio"
                    value="culqi"
                    name="pagos"
                    checked={this.state.value === "option3"}
                    style={{ marginRight: 20 }}
                  />
                  Pago Online
                </label>
              </div>
            </div>
          </div>
        </div>

        <div className="container">
          <div className="col-md-12 m-auto  text-center p-2" align="center">
            <br />
            <br />
            <br />
            <img
              src="/assets/icons/grupo.svg"
              className="img-fluid"
              alt="cello"
            />
          </div>
        </div>

        <Footer sectionR={false} />
      </div>
    );
  }
}
