import React from "react";
import Vimeo from "@u-wave/react-vimeo";
import { getAlbum } from "../services/vimeo";

import FormControl from "@material-ui/core/FormControl";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";
import SkipNextIcon from "@material-ui/icons/SkipNext";
import SkipPreviousIcon from "@material-ui/icons/SkipPrevious";
import AttachFileIcon from "@material-ui/icons/AttachFile";
import Switch from "react-switch";

import "./vimeo.css";

export default class VimeoPlayer extends React.Component {
  constructor() {
    super();
    this.state = {
      playList: null,
      playSelected: 0,
      enableAutoPlay: true,
      autoPlay: true
    };
  }

  componentDidMount() {
    getAlbum(6623200).then(res => {
      let data;
      if (res.info) {
        data = res;
        data.info.reverse();
      }
      this.setState({
        playList: data
      });
      console.log(data);
    });
  }

  _renderVimeoLayout = () => {
    if (
      this.state.playList &&
      this.state.playList.info &&
      this.state.playList.info.length > 0
    ) {
      return (
        <div>
          <Vimeo
            id="getvimeo"
            video={this.state.playList.info[this.state.playSelected].id}
            autoplay={this.state.autoPlay}
            responsive
            onEnd={() => {
              if (this.state.autoPlay) {
                if (
                  this.state.playSelected !==
                  this.state.playList.info.length - 1
                ) {
                  this.setState({ playSelected: this.state.playSelected + 1 });
                }
              }
            }}
            height={250}
          />
          <div
            style={{
              height: 70,
              width: "100%",
              backgroundColor: "#333333",
              marginTop: 12,
              display: "flex",
              alignItems: "center",
              paddingLeft: 12,
              justifyContent: "space-between"
            }}
          >
            {this.state.playList &&
              this.state.playList.info &&
              this.state.playList.info.length > 0 && (
                <label style={{ color: "white", fontSize: 18 }}>
                  {this.state.playList.info[this.state.playSelected].name}
                </label>
              )}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
              }}
            >
              <div
                style={{
                  marginRight: 16
                }}
              >
                <Button variant="contained">Asesoria</Button>
              </div>
              <div
                style={{
                  marginRight: 16
                }}
              >
                <Button variant="contained">
                  <AttachFileIcon />
                  Materiales
                </Button>
              </div>
              <div
                style={{
                  marginRight: 10,
                  cursor: "pointer",
                  fontSize: 18,
                  color: "#1d99f1"
                }}
              >
                <SkipPreviousIcon />
                <label
                  style={{ color: "white", margin: 0, cursor: "pointer" }}
                  onClick={() => {
                    if (this.state.playSelected === 0) {
                      return null;
                    } else {
                      this.setState({
                        playSelected: this.state.playSelected - 1
                      });
                    }
                  }}
                >
                  Anterior
                </label>
              </div>
              <div
                style={{
                  marginRight: 10,
                  cursor: "pointer",
                  fontSize: 18,
                  color: "#1d99f1"
                }}
              >
                <label
                  style={{ color: "white", margin: 0, cursor: "pointer" }}
                  onClick={() => {
                    if (
                      this.state.playSelected ===
                      this.state.playList.info.length - 1
                    ) {
                      return null;
                    } else {
                      this.setState({
                        playSelected: this.state.playSelected + 1
                      });
                    }
                  }}
                >
                  Siguiente
                </label>
                <SkipNextIcon />
              </div>
            </div>
          </div>
        </div>
      );
    }
  };

  _renderListLayout = () => {
    if (
      this.state.playList &&
      this.state.playList.info &&
      this.state.playList.info.length > 0
    ) {
      return this.state.playList.info.map((el, ind) => {
        return (
          <div
            style={{
              height: 55,
              width: "100%",
              marginBottom: 8,
              color: "white",
              display: "flex",
              alignItems: "center",
              padding: 4,
              cursor: "pointer",
              borderRightColor: "#1d99f1",
              borderRightWidth: this.state.playSelected === ind ? 3 : 0,
              borderRightStyle: "solid",
              fontSize: 13
            }}
            onClick={() => {
              this.setState({
                playSelected: ind
              });
            }}
          >
            {this.state.playSelected === ind && <div id="triangle-right"></div>}

            {el.name}
          </div>
        );
      });
    }
  };

  render() {
    console.log("autoPlay: ", this.state.autoPlay);
    return (
      <div
        id="vimeo-container"
        style={{
          display: "flex",
          height: "93.8%",
          backgroundColor: "#282828"
        }}
      >
        <div
          id="vimeo-row-1"
          style={{
            backgroundColor: "#282828",
            padding: 16
          }}
        >
          {this._renderVimeoLayout()}
        </div>
        <div
          id="vimeo-row-2"
          style={{
            backgroundColor: "#282828",
            padding: 12
          }}
        >
          <div>
            <div
              style={{
                flexDirection: "row",
                display: "flex"
              }}
            >
              <div
                style={{
                  color: "white",
                  fontSize: 16,
                  flex: 2,
                  height: 55,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  marginTop: 35
                }}
              >
                Elegir Curso
              </div>
              <div
                style={{
                  flex: 1,
                  display: "flex",
                  flexFlow: "row",
                  justifyContent: "center",
                  alignItems: "center",
                  color: "white"
                }}
              >
                autoplay
                <div style={{ width: 8 }} />
                <Switch
                  checked={this.state.autoPlay}
                  onChange={e => {
                    this.setState({ autoPlay: e });
                  }}
                  onColor={"#1d99f1"}
                  uncheckedIcon={false}
                  checkedIcon={false}
                  height={17}
                  width={34}
                />
              </div>
            </div>
          </div>
          <FormControl className="vimeo-selecter">
            <Select value={10} onChange={() => {}}>
              <MenuItem value={10}>MATEMÁTICA II</MenuItem>
              <MenuItem value={20}>MATEMÁTICA I</MenuItem>
              <MenuItem value={30}>RAZ. MATEMÁTICO</MenuItem>
              <MenuItem value={30}>PSICOLOGÍA</MenuItem>
              <MenuItem value={30}>PROCESOS HISTÓRICOS</MenuItem>
            </Select>
          </FormControl>
          <div
            style={{
              backgroundColor: "#333333"
            }}
          >
            {this._renderListLayout()}
          </div>
        </div>
      </div>
    );
  }
}
